import Foundation
import MovieAppWebService

final class MoviesInteractor: AssemblerWireframeInteractorProtocol {
    
    private var usecase: MoviesUsecase?
}

// MARK: - Extensions -

extension MoviesInteractor: MoviesInteractorInterface {
    
    func getSearchedMovies(callback: MoviesCallback, params: MoviesRequestModel) {
        DispatchQueue.main.asyncAfter(deadline: .now() + 1) {
            self.usecase = self.returnResolver().resolve(MoviesUsecase.self)
            self.usecase?.execute(useCaseCallBack: callback, params: params)
        }
    }
}
